function getScreenHeight(){return window.screen.availHeight}$(function(){$(document).keydown(function(event){if(event.keyCode=='122'){window.location.reload()}});$(window).on('scroll',function(){var point=getScreenHeight()+$(this).attr("scrollY");if(point>3456){$(".my-productions img").each(function(i){$(this).attr('src',$(this).data("src"))})}});$('.main').css('height',getScreenHeight()*6+'px');$('.me').on({'mouseover':function(){$('.contact').fadeIn()},'mouseout':function(){$('.contact').fadeOut()}});$('#info span').click(function(){$('html,body').animate({scrollTop:getScreenHeight()},1000)});$.each($('nav a'),function(i,n){$('nav a:eq('+i+')').click(function(){$('html,body').stop().animate({scrollTop:i*getScreenHeight()},1000)})});$(window).scroll(function(){if($(this).attr("scrollY")>getScreenHeight()-100){$('#to-Top').fadeIn()}else{$('#to-Top').fadeOut(function(){$(this).css('top','585px')})}});$('#to-Top').click(function(){if($(this).css('top')=='585px'){$(this).css('top','100px');$('html,body').stop().animate({scrollTop:'0'},600)}});var flag=false;var $ulObj=$(".my-productions > ul");var limit=($(".my-productions > ul >li").length-1)*360;$(".left-arrow").on('click',function(){var nowOffset=$ulObj.css('marginLeft');if(parseInt(nowOffset)%360==0){flag=true};if(flag){Math.abs(parseInt(nowOffset))>0?$ulObj.css('marginLeft',parseInt(nowOffset)+360):false;flag=false}});$(".right-arrow").on('click',function(){var nowOffset=$ulObj.css('marginLeft');if(parseInt(nowOffset)%360==0){flag=true};if(flag){Math.abs(parseInt(nowOffset))>=limit?false:$ulObj.css('marginLeft',parseInt(nowOffset)-360);flag=false}})});